import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import { ChatContext } from '../context/ChatContext';
import axios from 'axios';
import './ChatApp.css';
import ChatWindow from './ChatWindow/ChatWindow';

const ChatApp = () => {
  const { user, logout } = useContext(AuthContext);
  const { selectedChat, socket } = useContext(ChatContext);
  const [users, setUsers] = useState([]);

  // ‚úÖ FIXED: Fetch users from REST API
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/auth/users', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUsers(response.data);
        console.log('‚úÖ Users loaded:', response.data);
      } catch (error) {
        console.error('‚ùå Failed to fetch users:', error);
      }
    };

    if (user) {
      fetchUsers(); // Load users
      
      // Join socket room
      if (socket) {
        socket.emit('join', user._id);
        console.log('üì° Joined socket room:', user.id);
      }
    }
  }, [user, socket]);

  return (
    <div className="chat-app">
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>Chats</h2>
          <button onClick={logout}>Logout</button>
        </div>
        <div className="user-list">
          {users.length === 0 ? (
            <div>No users found</div>
          ) : (
            users.map(u => (
              <div key={u._id} className="user-item">
                <strong>{u.username}</strong>
                <span className={u.isOnline ? 'online' : 'offline'}>
                  {u.isOnline ? 'üü¢' : 'üî¥'}
                </span>
              </div>
            ))
          )}
        </div>
      </div>
      {selectedChat ? (
        <ChatWindow />
      ) : (
        <div className="no-chat-selected">
          <h2>Select a chat to start messaging</h2>
          <p>üëà Choose from the list</p>
        </div>
      )}
    </div>
  );
};

export default ChatApp;
