import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import { ChatContext } from '../context/ChatContext';
import axios from 'axios';
import './ChatApp.css';
import ChatWindow from './ChatWindow/ChatWindow';

const ChatApp = () => {
  const { user, logout } = useContext(AuthContext);
  const { selectedChat, socket } = useContext(ChatContext);
  const [users, setUsers] = useState([]);

  // âœ… FIXED: Fetch users from REST API
useEffect(() => {
  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/auth/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(response.data);
      console.log('âœ… Users loaded:', response.data);
    } catch (error) {
      console.error('âŒ Failed to fetch users:', error);
    }
  };

  if (user) {
    fetchUsers();
    
    if (socket) {
      socket.emit('join', user._id);  // âœ… FIXED - using _id
      console.log('ðŸ“¡ Joined socket room:', user._id);
      
      // ðŸ”¥ GREEN DOTS LISTENERS
      socket.on('userOnline', (onlineUserId) => {
        setUsers(prev => prev.map(u => 
          u._id === onlineUserId ? { ...u, isOnline: true } : u
        ));
      });
      
      socket.on('userOffline', (offlineUserId) => {
        setUsers(prev => prev.map(u => 
          u._id === offlineUserId ? { ...u, isOnline: false } : u
        ));
      });
    }
  }

  // Cleanup
  return () => {
    socket?.off('userOnline');
    socket?.off('userOffline');
  };
}, [user, socket]);


  return (
    <div className="chat-app">
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>Chats</h2>
          <button onClick={logout}>Logout</button>
        </div>
        <div className="user-list">
          {users.length === 0 ? (
            <div>No users found</div>
          ) : (
           users.map(u => (
  <div 
    key={u._id} 
    className="user-item"
    style={{ cursor: 'pointer', padding: '10px' }} // Make clickable
    onClick={() => {
      // ðŸ”¥ CORRECT ChatContext syntax
      selectedChat.setChat(u);  // or selectedChat.set(u)
      console.log('ðŸ’¬ Chat selected:', u.username);
    }}
  >
    <strong>{u.username}</strong>
    <span className={u.isOnline ? 'online' : 'offline'}>
      {u.isOnline ? 'ðŸŸ¢' : 'ðŸ”´'}
    </span>
  </div>
))

          )}
        </div>
      </div>
      {selectedChat ? (
        <ChatWindow />
      ) : (
        <div className="no-chat-selected">
          <h2>Select a chat to start messaging</h2>
          <p>ðŸ‘ˆ Choose from the list</p>
        </div>
      )}
    </div>
  );
};

export default ChatApp;
