import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import { ChatContext } from '../context/ChatContext';
import axios from 'axios';
import './ChatApp.css';
import ChatWindow from './ChatWindow/ChatWindow';

const ChatApp = () => {
  const { user, logout } = useContext(AuthContext);
  const { socket } = useContext(ChatContext);
  const [users, setUsers] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null); // ðŸ”¥ LOCAL STATE!

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/auth/users', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUsers(response.data);
        console.log('âœ… Users loaded:', response.data);
      } catch (error) {
        console.error('âŒ Failed to fetch users:', error);
      }
    };

    if (user) {
      fetchUsers();
      
      if (socket) {
        socket.emit('join', user._id);
        console.log('ðŸ“¡ Joined socket room:', user._id);
        
        socket.on('userOnline', (onlineUserId) => {
          setUsers(prev => prev.map(u => 
            u._id === onlineUserId ? { ...u, isOnline: true } : u
          ));
        });
        
        socket.on('userOffline', (offlineUserId) => {
          setUsers(prev => prev.map(u => 
            u._id === offlineUserId ? { ...u, isOnline: false } : u
          ));
        });
      }
    }

    return () => {
      socket?.off('userOnline');
      socket?.off('userOffline');
    };
  }, [user, socket]);

  const selectChat = (chatUser) => {
    setSelectedChat(chatUser); // âœ… LOCAL STATE - NO ERRORS!
    console.log('ðŸ’¬ Chat selected:', chatUser.username);
  };

  return (
    <div className="chat-app">
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>Chats</h2>
          <button onClick={logout}>Logout</button>
        </div>
        <div className="user-list">
          {users.length === 0 ? (
            <div>No users found</div>
          ) : (
            users.map(u => (
              <div 
                key={u._id}
                className={`user-item ${u.isOnline ? 'online' : ''}`}
                style={{ 
                  cursor: 'pointer', 
                  padding: '12px', 
                  border: '1px solid #eee',
                  margin: '5px 0',
                  borderRadius: '8px',
                  background: u.isOnline ? '#e8f5e8' : '#f5f5f5'
                }}
                onClick={() => selectChat(u)} // âœ… 100% WORKING!
              >
                <strong style={{ marginRight: '10px' }}>{u.username}</strong>
                <span style={{ fontSize: '20px' }}>
                  {u.isOnline ? 'ðŸŸ¢' : 'ðŸ”´'}
                </span>
              </div>
            ))
          )}
        </div>
      </div>
      
      {selectedChat ? (
        <ChatWindow chatUser={selectedChat} /> // Pass chatUser to ChatWindow
      ) : (
        <div className="no-chat-selected" style={{ padding: '40px', textAlign: 'center' }}>
          <h2>Select a chat to start messaging</h2>
          <p style={{ color: '#666' }}>ðŸ‘ˆ Click any user to start chatting!</p>
        </div>
      )}
    </div>
  );
};

export default ChatApp;
