import React from 'react';
import './ChatWindow.css';

const MessageList = ({ messages }) => {
  return (
    <div className="message-list">
      {messages.length === 0 ? (
        <div style={{ padding: '20px', textAlign: 'center', color: '#999' }}>
          No messages yet. Start the conversation!
        </div>
      ) : (
        messages.map((msg, index) => (
          <div key={msg.timestamp || index} className="message">
            {/* ✅ FIX 1: username OR sender */}
            <strong>{msg.username || msg.sender?.username || msg.sender || 'Unknown'}</strong>
            {/* ✅ FIX 2: message OR content */}
            <p>{msg.message || msg.content || 'No message'}</p>
            {/* ✅ Time already working perfectly */}
            <small>{new Date(msg.timestamp).toLocaleTimeString()}</small>
          </div>
        ))
      )}
    </div>
  );
};

export default MessageList;
