import React, { useState, useContext } from 'react';
import { ChatContext } from '../context/ChatContext'; // ← ChatContext only

export default function Login({ onSwitch }) {
  const { login, register } = useContext(ChatContext);
  const [isRegister, setIsRegister] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    setError(''); // Clear error
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    let result;
    if (isRegister) {
      result = await register(formData.username, formData.email, formData.password);
    } else {
      result = await login(formData.email, formData.password);
    }

    if (!result.success) {
      setError(result.message);
    }
    setLoading(false);
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>{isRegister ? 'Register' : 'Login'}</h2>
        
        <form onSubmit={handleSubmit}>
          {isRegister && (
            <div className="form-group">
              <label>Username</label>
              <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleChange}
                placeholder="Enter username"
                required
              />
            </div>
          )}
          
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter email"
              required
            />
          </div>
          
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter password"
              required
            />
          </div>

          {error && <p style={{color: 'red'}}>{error}</p>}

          <button type="submit" disabled={loading} className={isRegister ? 'register-btn' : 'login-btn'}>
            {loading ? 'Loading...' : (isRegister ? 'Register' : 'Login')}
          </button>
        </form>

        <button 
          onClick={() => setIsRegister(!isRegister)}
          style={{marginTop: '10px', width: '100%'}}
        >
          {isRegister ? 'Back to Login' : "Don't have account? Register"}
        </button>
      </div>
    </div>
  );
};
