import React from 'react';
import './ChatWindow.css';

const MessageList = ({ messages, currentUserId }) => {  // Add currentUserId prop
  return (
    <div className="message-list">
      {messages.length === 0 ? (
        <div style={{ padding: '20px', textAlign: 'center', color: '#999' }}>
          No messages yet. Start the conversation!
        </div>
      ) : (
        messages.map((msg, index) => (
          <div 
            key={msg.timestamp || index} 
            className={`message ${msg.from === currentUserId ? 'sent' : 'received'}`}  // ← BUBBLE MAGIC!
          >
            <div className="message-content">
              <strong>{msg.username || msg.sender || 'Unknown'}</strong>
              <p>{msg.message || msg.content || 'No message'}</p>
              <small>{new Date(msg.timestamp).toLocaleTimeString()}</small>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default MessageList;
